INSERT OR IGNORE INTO recipes (name, ingredients, instructions) VALUES 
('Marua', 'Rice, Meat (chicken or pork), Onion, Ginger, Garlic, Green chilies, Soy sauce, Salt, Oil', '1. Cook rice and let it cool. 2. In a pan, heat oil and sauté chopped onions, ginger, and garlic until golden. 3. Add meat and cook until browned. 4. Add green chilies, soy sauce, and salt, and stir-fry until combined. 5. Mix in cooked rice and serve hot.'),
('Momo', 'All-purpose flour, Ground meat (chicken or pork), Onion, Ginger, Garlic, Green chilies, Soy sauce, Salt, Oil', '1. Prepare dough with all-purpose flour and water, and let it rest. 2. Mix ground meat with onion, ginger, garlic, green chilies, soy sauce, and salt. 3. Roll out small circles of dough, fill with the meat mixture, and fold to seal. 4. Steam the momos for about 15-20 minutes. 5. Serve hot with dipping sauce.'),
('Ngatok', 'Rice, Meat (chicken or pork), Onion, Ginger, Garlic, Green chilies, Soy sauce, Salt, Oil', '1. Cook rice and let it cool. 2. In a pan, heat oil and sauté chopped onions, ginger, and garlic until golden. 3. Add meat and cook until browned. 4. Add green chilies, soy sauce, and salt, and stir-fry until combined. 5. Mix in cooked rice and serve hot.'),
('Thenthuk', 'Noodles, Meat (chicken or pork), Onion, Ginger, Garlic, Green chilies, Soy sauce, Salt, Oil', '1. Cook noodles and let them cool. 2. In a pan, heat oil and sauté chopped onions, ginger, and garlic until golden. 3. Add meat and cook until browned. 4. Add green chilies, soy sauce, and salt, and stir-fry until combined. 5. Mix in cooked noodles and serve hot.'),
('Wungwunt Ngam', 'Rice, Meat (chicken or pork), Onion, Ginger, Garlic, Green chilies, Soy sauce, Salt, Oil', '1. Cook rice and let it cool. 2. In a pan, heat oil and sauté chopped onions, ginger, and garlic until golden. 3. Add meat and cook until browned. 4. Add green chilies, soy sauce, and salt, and stir-fry until combined. 5. Mix in cooked rice and serve hot.'),
('Gyapa Khazi', 'All-purpose flour, Water, Salt, Oil', '1. Mix all-purpose flour with water and salt to form a dough. 2. Roll out the dough and cut into desired shapes. 3. Deep fry until golden brown and serve as a snack.'),
('Chura Kampo', 'Rice, Butter, Salt, Water', '1. Cook rice and let it cool. 2. In a pan, melt butter and add cooked rice. 3. Stir well and season with salt. 4. Serve warm as a side dish.'),
('Poora Haah', 'Rice flour, Water, Salt', '1. Mix rice flour with water and salt to form a batter. 2. Heat a pan and pour the batter to form a pancake. 3. Cook until golden brown on both sides and serve hot.'),
('Panta Bhat', 'Cooked rice, Water, Salt', '1. Soak cooked rice in water overnight. 2. Drain excess water and season with salt. 3. Serve cold or at room temperature, often with green chilies and onion.'),
('Masoor Tenga', 'Fish, Tenga (a type of citrus fruit), Mustard oil, Salt, Turmeric powder, Red chili powder', '1. Clean and cut the fish into small pieces. 2. Heat mustard oil in a pan and sauté the fish until golden brown. 3. Add tenga juice, salt, turmeric powder, and red chili powder. 4. Simmer until the fish is cooked through. 5. Serve hot with rice.'),
('Aloo Pitika', 'Potatoes, Mustard oil, Salt, Turmeric powder, Red chili powder, Coriander leaves', '1. Boil and mash the potatoes. 2. Heat mustard oil in a pan and sauté the mashed potatoes until golden brown. 3. Add salt, turmeric powder, and red chili powder. 4. Mix well and garnish with coriander leaves. 5. Serve hot with rice.'),
('Bhut Jolokia Chicken', 'Chicken, Bhut jolokia (ghost pepper), Mustard oil, Salt, Turmeric powder, Red chili powder', '1. Clean and cut the chicken into small pieces. 2. Heat mustard oil in a pan and sauté the chicken until golden brown. 3. Add bhut jolokia, salt, turmeric powder, and red chili powder. 4. Simmer until the chicken is cooked through. 5. Serve hot with rice.'),
('Pitha', 'Rice flour, Water, Salt, Mustard oil', '1. Mix rice flour with water and salt to form a dough. 2. Heat mustard oil in a pan and fry the dough until golden brown. 3. Serve hot with tea or coffee.'),
('Jol Moley', 'Fish, Mustard oil, Salt, Turmeric powder, Red chili powder', '1. Clean and cut the fish into small pieces. 2. Heat mustard oil in a pan and sauté the fish until golden brown. 3. Add salt, turmeric powder, and red chili powder. 4. Simmer until the fish is cooked through. 5. Serve hot with rice.'),
('Dhekia Xak', 'Fiddlehead ferns, Mustard oil, Salt, Turmeric powder, Red chili powder', '1. Clean and cut the fiddlehead ferns into small pieces. 2. Heat mustard oil in a pan and sauté the ferns until golden brown. 3. Add salt, turmeric powder, and red chili powder. 4. Simmer until the ferns are cooked through. 5. Serve hot with rice.'),
('Koldil Chicken', 'Chicken, Koldil (a type of leafy green), Mustard oil, Salt, Turmeric powder, Red chili powder', '1. Clean and cut the chicken into small pieces. 2. Heat mustard oil in a pan and sauté the chicken until golden brown. 3. Add koldil, salt, turmeric powder, and red chili powder. 4. Simmer until the chicken is cooked through. 5. Serve hot with rice.'),
('Mitha Alu', 'Potatoes, Mustard oil, Salt, Turmeric powder, Red chili powder, Coriander leaves', '1. Boil and mash the potatoes. 2. Heat mustard oil in a pan and sauté the mashed potatoes until golden brown. 3. Add salt, turmeric powder, and red chili powder. 4. Mix well and garnish with coriander leaves. 5. Serve hot with rice.'),
('Bhendir Sorsori', 'Okra, Mustard oil, Salt, Turmeric powder, Red chili powder', '1. Clean and cut the okra into small pieces. 2. Heat mustard oil in a pan and sauté the okra until golden brown. 3. Add salt, turmeric powder, and red chili powder. 4. Simmer until the okra is cooked through. 5. Serve hot with rice.'),
('Doi Maach', 'Fish, Yogurt, Onion, Ginger, Garlic, Turmeric powder, Red chili powder, Salt, Mustard oil', '1. Marinate fish with salt, turmeric, and red chili powder. 2. Heat mustard oil in a pan and fry the fish until golden. 3. In the same oil, sauté chopped onions, ginger, and garlic. 4. Add yogurt and mix well. 5. Add the fried fish and simmer for a few minutes. 6. Serve hot with rice.'),
('Bhut Jolokia Pickle', 'Bhut jolokia (ghost pepper), Mustard oil, Salt, Turmeric powder, Vinegar', '1. Chop bhut jolokia and mix with salt, turmeric powder, and vinegar. 2. Heat mustard oil and pour over the mixture. 3. Store in a jar and let it marinate for a few days before serving.'), 
('Bilahi Ombol', 'Bilahi (a type of citrus fruit), Mustard oil, Salt, Turmeric powder, Red chili powder', '1. Cut bilahi into small pieces and mix with salt, turmeric powder, and red chili powder. 2. Heat mustard oil in a pan and sauté the bilahi mixture until golden brown. 3. Serve as a side dish with rice.'),
('Maati Daali', 'Split red lentils, Mustard oil, Salt, Turmeric powder, Red chili powder', '1. Cook split red lentils until soft. 2. Heat mustard oil in a pan and sauté the cooked lentils until golden brown. 3. Add salt, turmeric powder, and red chili powder. 4. Mix well and serve as a side dish with rice.'),
('Cabbage Potato Fry', 'Cabbage, Potatoes, Mustard oil, Salt, Turmeric powder, Red chili powder', '1. Cut cabbage and potatoes into small pieces. 2. Heat mustard oil in a pan and sauté the cabbage and potatoes until golden brown. 3. Add salt, turmeric powder, and red chili powder. 4. Mix well and serve as a side dish with rice.'),
('Bhog Khichri', 'Rice, Split red lentils, Mustard oil, Salt, Turmeric powder, Red chili powder', '1. Cook rice and split red lentils together until soft. 2. Heat mustard oil in a pan and sauté the cooked mixture until golden brown. 3. Add salt, turmeric powder, and red chili powder. 4. Mix well and serve as a main dish.'),
('Ghugni', 'Chickpeas, Mustard oil, Salt, Turmeric powder, Red chili powder', '1. Cook chickpeas until soft. 2. Heat mustard oil in a pan and sauté the cooked chickpeas until golden brown. 3. Add salt, turmeric powder, and red chili powder. 4. Mix well and serve as a snack or side dish.'),
('Duck Meat Curry', 'Duck meat, Mustard oil, Salt, Turmeric powder, Red chili powder, Coriander powder', '1. Clean and cut the duck meat into small pieces. 2. Heat mustard oil in a pan and sauté the duck meat until golden brown. 3. Add salt, turmeric powder, red chili powder, and coriander powder. 4. Mix well and simmer until the meat is cooked through. 5. Serve hot with rice.'),
('Luchi', 'All-purpose flour, Water, Salt, Mustard oil', '1. Mix all-purpose flour with water and salt to form a dough. 2. Knead the dough until smooth. 3. Roll out small balls of dough and flatten them. 4. Heat mustard oil in a pan and fry the flattened dough until golden brown. 5. Serve hot with dal or curry.'),
('Til Gahori', 'Sesame seeds, Jaggery, Water, Mustard oil', '1. Mix sesame seeds with jaggery and water to form a paste. 2. Heat mustard oil in a pan and sauté the paste until golden brown. 3. Serve as a snack or dessert.'),
('Pani Pitha', 'Rice flour, Water, Jaggery, Coconut, Salt', '1. Mix rice flour with water to form a dough. 2. Prepare a filling with grated coconut and jaggery. 3. Shape the dough into small discs, place filling inside, and fold. 4. Steam until cooked through and serve warm.'),
('Kardoi', 'Banana, Mustard oil, Salt, Turmeric powder, Red chili powder', '1. Cut banana into small pieces and mix with salt, turmeric powder, and red chili powder. 2. Heat mustard oil in a pan and sauté the banana mixture until golden brown. 3. Serve as a side dish with rice.'),
('Undhiyu', 'Mixed vegetables (like eggplant, potatoes, and beans), Green peas, Methi (fenugreek) leaves, Spices (cumin, coriander, turmeric), Coconut, Ginger, Garlic, Green chilies, Oil, Salt', '1. Heat oil in a pot and add cumin seeds. 2. Add chopped ginger, garlic, and green chilies, and sauté. 3. Add chopped vegetables and spices, and mix well. 4. Add coconut and water, cover, and cook until the vegetables are tender. 5. Serve hot with puris or rotis.'),
('Methi Thepla', 'Whole wheat flour, Methi (fenugreek) leaves, Yogurt, Turmeric powder, Red chili powder, Salt, Oil', '1. Mix whole wheat flour, chopped methi leaves, yogurt, turmeric powder, red chili powder, and salt to form a dough. 2. Roll out into thin flatbreads. 3. Cook on a hot griddle with a little oil until golden brown on both sides. 4. Serve hot with yogurt or pickle.'),
('Dhokla', 'Chickpea flour (besan), Yogurt, Turmeric powder, Green chilies, Ginger, Eno (fruit salt), Salt, Mustard seeds, Curry leaves', '1. Mix chickpea flour, yogurt, turmeric powder, green chilies, ginger, and salt to form a batter. 2. Add Eno and mix well. 3. Pour into a greased steaming tray and steam for 15-20 minutes. 4. Temper with mustard seeds and curry leaves, and serve with green chutney.'),
('Fafda', 'Chickpea flour (besan), Turmeric powder, Carom seeds (ajwain), Salt, Oil', '1. Mix chickpea flour, turmeric powder, carom seeds, and salt. 2. Add water to form a stiff dough. 3. Roll out thin strips and deep fry until crispy. 4. Serve hot with green chutney and papaya salad.'),
('Khandvi', 'Chickpea flour (besan), Yogurt, Turmeric powder, Ginger paste, Salt, Mustard seeds, Coconut, Coriander leaves', '1. Mix chickpea flour, yogurt, turmeric powder, ginger paste, and salt with water to form a smooth batter. 2. Cook on low heat until thickened, then spread on a greased surface. 3. Roll into tight rolls and cut into pieces. 4. Temper with mustard seeds and garnish with coconut and coriander leaves.'),
('Handvo', 'Rice, Split urad dal, Yogurt, Vegetables (like bottle gourd), Ginger, Green chilies, Mustard seeds, Salt', '1. Soak rice and urad dal overnight, then grind to a coarse batter. 2. Mix in yogurt, grated vegetables, ginger, green chilies, and salt. 3. Pour into a greased baking dish and bake until golden brown. 4. Temper with mustard seeds and serve hot.'),
('Kadhi', 'Yogurt, Besan (chickpea flour), Turmeric powder, Ginger, Green chilies, Mustard seeds, Curry leaves, Salt', '1. Mix yogurt, besan, turmeric powder, ginger, and green chilies with water to form a smooth mixture. 2. Cook in a pot, stirring continuously until it thickens. 3. Temper with mustard seeds and curry leaves. 4. Serve hot with rice or roti.'),
('Ghugra', 'All-purpose flour, Chana dal (split chickpeas), Jaggery, Cardamom powder, Ghee, Salt', '1. Cook chana dal until soft and mash it. 2. Mix with jaggery and cardamom powder. 3. Prepare a dough with all-purpose flour, ghee, and salt. 4. Roll out small discs, fill with the dal mixture, and fold into half-moons. 5. Deep fry until golden brown and serve hot.'),
('Sev Tameta', 'Tomatoes, Sev (crispy chickpea noodles), Green chilies, Ginger, Mustard seeds, Turmeric powder, Salt, Oil', '1. Heat oil in a pan and add mustard seeds. 2. Once they splutter, add chopped ginger and green chilies. 3. Add chopped tomatoes, turmeric powder, and salt, and cook until the tomatoes are soft. 4. Top with sev and serve hot with roti or rice.'),
('Bajra Khichdi', 'Bajra (pearl millet), Moong dal, Ginger, Green chilies, Turmeric powder, Salt, Ghee', '1. Rinse bajra and moong dal, then soak for a few hours. 2. In a pressure cooker, heat ghee and add ginger and green chilies. 3. Add soaked bajra and moong dal, turmeric powder, and salt. 4. Add water and cook until soft. 5. Serve hot with ghee and pickle.'),
('Patra', 'Colocasia leaves, Gram flour (besan), Spices (coriander powder, turmeric powder, red chili powder), Jaggery, Salt', '1. Mix gram flour with spices, jaggery, and water to form a paste. 2. Spread the paste on colocasia leaves and roll tightly. 3. Steam the rolls for 20-30 minutes. 4. Slice and serve with chutney.'),
('Doodhpak', 'Rice, Milk, Sugar, Cardamom, Cashews, Raisins', '1. Boil milk in a pan. 2. Add washed rice and cook until soft. 3. Stir in sugar and cardamom, and cook for a few more minutes. 4. Garnish with roasted cashews and raisins. 5. Serve warm or chilled as a dessert.'),
('Chaas', 'Yogurt, Water, Cumin powder, Salt, Mint leaves', '1. Whisk yogurt with water until smooth. 2. Add cumin powder and salt, and mix well. 3. Garnish with chopped mint leaves. 4. Serve chilled as a refreshing drink.'),
('Khaman Dhokla', 'Chickpea flour (besan), Yogurt, Turmeric powder, Eno (fruit salt), Green chilies, Mustard seeds, Salt', '1. Mix chickpea flour, yogurt, turmeric powder, and salt with water to form a batter. 2. Add Eno and mix well. 3. Pour into a greased steaming tray and steam for 15-20 minutes. 4. Temper with mustard seeds and serve with green chutney.'),
('Bajra Roti', 'Bajra flour, Water, Salt', '1. Mix bajra flour with water and salt to form a dough. 2. Roll out into flatbreads. 3. Cook on a hot griddle until golden brown on both sides. 4. Serve hot with ghee and jaggery or curry.'),
('Dal Dhokli', 'Whole wheat flour, Toor dal (split pigeon peas), Turmeric powder, Red chili powder, Mustard seeds, Curry leaves, Salt, Jaggery', '1. Cook toor dal with turmeric and salt until soft. 2. Prepare a dough with whole wheat flour and roll out into thin discs. 3. Cut the discs into strips and add them to the boiling dal. 4. Cook until the dhokli is soft. 5. Temper with mustard seeds and curry leaves, and serve hot.'),
('Kadhi Pakora', 'Yogurt, Besan (chickpea flour), Onion, Ginger, Green chilies, Turmeric powder, Salt, Mustard seeds, Curry leaves', '1. Mix yogurt, besan, turmeric powder, and salt with water to form a smooth mixture. 2. Cook in a pot, stirring continuously until it thickens. 3. For pakoras, mix besan with chopped onions, green chilies, and salt, and deep fry until golden. 4. Add pakoras to the kadhi and simmer. 5. Serve hot with rice.'),
('Dal Baati Churma', 'Wheat flour, Ghee, Salt, Yellow lentils (moong dal or toor dal), Spices (cumin, turmeric, red chili powder), Jaggery, Cardamom', '1. Prepare the dough with wheat flour, ghee, and salt. 2. Shape into round balls (baati) and bake until golden. 3. Cook lentils with spices to make dal. 4. For churma, crush baati and mix with ghee, jaggery, and cardamom. 5. Serve dal with baati and churma.'),
('Gatte ki Sabzi', 'Gram flour (besan), Yogurt, Spices (cumin, coriander, turmeric), Green chilies, Ginger, Salt, Oil', '1. Make a dough with gram flour, spices, and water, then roll into cylindrical shapes and boil. 2. Cut into pieces and set aside. 3. In a pan, heat oil, add spices, and yogurt, then add gatte pieces. 4. Cook until well combined and serve hot with roti.'),
('Ker Sangri', 'Ker (dried berries), Sangri (dried beans), Spices (cumin, turmeric, red chili powder), Salt, Oil', '1. Soak ker and sangri in water for a few hours. 2. Heat oil in a pan, add spices, and sauté. 3. Add soaked ker and sangri, and cook until tender. 4. Serve hot with roti or bajra khichdi.'),
('Rajasthani Kadhi', 'Yogurt, Gram flour (besan), Spices (cumin, turmeric, red chili powder), Ginger, Green chilies, Salt, Oil', '1. Mix yogurt and gram flour with water to form a smooth batter. 2. Cook in a pot, stirring continuously until it thickens. 3. Temper with cumin seeds, ginger, and green chilies. 4. Serve hot with rice.'),
('Pyaaz Kachori', 'All-purpose flour, Onions, Spices (cumin, coriander, turmeric), Green chilies, Ginger, Salt, Oil', '1. Prepare a dough with all-purpose flour and water. 2. Sauté chopped onions with spices until soft. 3. Roll out small discs of dough, fill with onion mixture, and seal. 4. Deep fry until golden brown. 5. Serve hot with chutney.'),
('Churma Ladoo', 'Wheat flour, Ghee, Jaggery, Cardamom, Nuts (cashews, almonds)', '1. Prepare a dough with wheat flour and ghee, then bake or fry until golden. 2. Crush the cooked dough and mix with jaggery, cardamom, and nuts. 3. Shape into small balls (ladoo) and serve as a sweet treat.'),
('Laal Maas', 'Mutton, Red chili paste, Yogurt, Garlic, Ginger, Spices (cumin, coriander, turmeric), Salt, Oil', '1. Heat oil in a pot and sauté garlic and ginger. 2. Add mutton pieces and cook until browned. 3. Stir in red chili paste, yogurt, and spices. 4. Add water and simmer until the meat is tender. 5. Serve hot with rice or naan.'),
('Doodh Pak', 'Rice, Milk, Sugar, Cardamom, Cashews, Raisins', '1. Boil milk in a pan. 2. Add washed rice and cook until soft. 3. Stir in sugar and cardamom, and cook for a few more minutes. 4. Garnish with roasted cashews and raisins. 5. Serve warm or chilled as a dessert.'),
('Ghevar', 'All-purpose flour, Ghee, Water, Milk, Sugar, Cardamom powder, Saffron strands, Chopped nuts (almonds, pistachios), Honey (optional)', '1. In a bowl, mix all-purpose flour and ghee to form a crumbly mixture. 2. Gradually add water to make a smooth batter. 3. Heat ghee in a deep pan and pour the batter through a ladle with holes to create a circular shape. 4. Fry until golden brown and remove. 5. In another pan, prepare sugar syrup by boiling sugar and water until it reaches a one-string consistency. 6. Dip the fried ghevar in the syrup and garnish with cardamom powder, saffron, and chopped nuts. 7. Serve warm or at room temperature.'),
('Badam ka Halwa', 'Almonds, Ghee, Sugar, Milk, Cardamom powder, Saffron strands, Chopped nuts (for garnish)', '1. Soak almonds in hot water for a few hours, then peel and grind to a coarse paste. 2. Heat ghee in a pan and add the almond paste, cooking until it turns golden. 3. Add milk and sugar, and cook until the mixture thickens. 4. Stir in cardamom powder and saffron. 5. Cook until the halwa leaves the sides of the pan. 6. Garnish with chopped nuts and serve warm.'),
('Litti Chokha', 'Whole wheat flour, Sattu (roasted gram flour), Mustard oil, Spices (cumin, coriander, turmeric), Green chilies, Garlic, Eggplant, Tomato, Potato', '1. Prepare the dough with whole wheat flour and water. 2. Mix sattu with spices, mustard oil, and water to form a filling. 3. Shape the dough into balls, fill with the sattu mixture, and seal. 4. Bake the littis in a tandoor or oven until golden brown. 5. For chokha, roast eggplant, tomato, and potato, then mash and mix with spices and mustard oil. 6. Serve litti with chokha and chutney.'),
('Sattu Paratha', 'Whole wheat flour, Sattu (roasted gram flour), Green chilies, Ginger, Coriander leaves, Salt, Water, Ghee', '1. Prepare the dough with whole wheat flour and water. 2. Mix sattu with chopped green chilies, ginger, coriander leaves, and salt. 3. Roll out small discs of dough, fill with the sattu mixture, and seal. 4. Roll out again and cook on a hot griddle with ghee until golden brown. 5. Serve hot with yogurt or pickle.'),
('Bihari Kadhi', 'Yogurt, Gram flour (besan), Turmeric powder, Ginger, Green chilies, Mustard seeds, Curry leaves, Salt', '1. Mix yogurt and gram flour with water to form a smooth batter. 2. Cook in a pot, stirring continuously until it thickens. 3. Temper with mustard seeds, curry leaves, ginger, and green chilies. 4. Serve hot with rice.'),
('Chura Dahi', 'Flattened rice (chura), Yogurt, Jaggery, Banana, Salt', '1. Rinse flattened rice in water and drain. 2. Mix with yogurt, jaggery, and a pinch of salt. 3. Add sliced bananas and serve as a refreshing snack or breakfast.'),
('Aloo Tamatar ki Sabzi', 'Potatoes, Tomatoes, Green chilies, Ginger, Spices (cumin, coriander, turmeric), Salt, Oil', '1. Heat oil in a pan and add cumin seeds. 2. Add chopped ginger and green chilies, and sauté. 3. Add chopped tomatoes and spices, and cook until soft. 4. Add cubed potatoes and salt, and cook until the potatoes are tender. 5. Serve hot with roti or rice.'),
('Thekua', 'Whole wheat flour, Jaggery, Coconut, Ghee, Cardamom powder, Water', '1. Mix whole wheat flour, grated coconut, jaggery, and cardamom powder. 2. Add ghee and water to form a dough. 3. Shape into small discs and deep fry until golden brown. 4. Serve as a sweet snack.'),
('Bihari Chura', 'Flattened rice (chura), Mustard oil, Green chilies, Onion, Salt', '1. Rinse flattened rice in water and drain. 2. In a bowl, mix chura with chopped onions, green chilies, mustard oil, and salt. 3. Serve as a snack or light meal.'),
('Dushka', 'Rice flour, Water, Salt, Mustard oil, Green chilies, Onion, Spices (cumin, coriander)', '1. Mix rice flour with water and salt to form a smooth batter. 2. Heat oil in a pan and add cumin seeds. 3. Pour the batter into the pan and cook until golden brown on both sides. 4. Serve hot with chutney or pickle.'),
('Lauki Jabar', 'Bottle gourd (lauki), Mustard oil, Cumin seeds, Green chilies, Ginger, Garlic, Salt, Turmeric powder', '1. Peel and chop the bottle gourd into small pieces. 2. Heat mustard oil in a pan and add cumin seeds. 3. Add chopped ginger, garlic, and green chilies, and sauté. 4. Add bottle gourd, salt, and turmeric powder, and cook until tender. 5. Serve hot with roti or rice.'),
('Bihari Aloo Bhujiya', 'Potatoes, Mustard oil, Cumin seeds, Green chilies, Turmeric powder, Salt', '1. Peel and cut potatoes into thin slices. 2. Heat mustard oil in a pan and add cumin seeds. 3. Add sliced potatoes, green chilies, turmeric powder, and salt. 4. Cook until the potatoes are crispy and golden. 5. Serve hot as a snack or side dish.'),
('Bihari Qeema', 'Minced meat (mutton or chicken), Onions, Ginger, Garlic, Spices (cumin, coriander, garam masala), Green chilies, Salt, Oil', '1. Heat oil in a pan and sauté chopped onions, ginger, and garlic until golden. 2. Add minced meat and cook until browned. 3. Stir in spices and salt, and cook until the meat is tender. 4. Serve hot with roti or paratha.'),
('Lauki ka Raita', 'Bottle gourd (lauki), Yogurt, Cumin powder, Salt, Green chilies, Coriander leaves', '1. Grate the bottle gourd and squeeze out excess water. 2. In a bowl, mix yogurt, grated lauki, cumin powder, salt, and chopped green chilies. 3. Garnish with coriander leaves. 4. Serve chilled as a side dish.'),
('Veg Boti Biryani', 'Basmati rice, Mixed vegetables (carrots, peas, beans), Spices (bay leaf, cloves, cardamom), Ginger, Garlic, Onion, Salt, Oil', '1. Heat oil in a pot and sauté chopped onions, ginger, and garlic until golden. 2. Add mixed vegetables and spices, and cook for a few minutes. 3. Add soaked basmati rice and water, and cook until the rice is done. 4. Serve hot with raita or salad.'),
('Parval Chokha', 'Pointed gourd (parval), Mustard oil, Garlic, Green chilies, Salt, Onion', '1. Boil or roast the pointed gourd until soft. 2. Mash it with chopped garlic, green chilies, and salt. 3. Heat mustard oil in a pan and sauté chopped onions until golden. 4. Mix the mashed parval with sautéed onions. 5. Serve hot with roti or rice.'),
('Gur Anarsa', 'Rice flour, Jaggery, Sesame seeds, Ghee, Water', '1. Mix rice flour with jaggery and water to form a dough. 2. Shape into small discs and coat with sesame seeds. 3. Heat ghee in a pan and deep fry until golden brown. 4. Serve as a sweet snack.'),
('Kundru ki Bhujiya', 'Ivy gourd (kundru), Mustard oil, Cumin seeds, Green chilies, Turmeric powder, Salt', '1. Wash and slice the kundru into thin pieces. 2. Heat mustard oil in a pan and add cumin seeds. 3. Add sliced kundru, green chilies, turmeric powder, and salt. 4. Cook until the kundru is tender and slightly crispy. 5. Serve hot as a side dish.'),
('Aloo Bukhar Chutney', 'Dried plums (aloo bukhar), Green chilies, Ginger, Jaggery, Salt, Water', '1. Soak dried plums in water for a few hours. 2. Blend soaked plums with green chilies, ginger, jaggery, and salt to a smooth paste. 3. Adjust consistency with water if needed. 4. Serve as a tangy chutney with snacks or meals.'),
('Thadal', 'Water, Almonds, Poppy seeds, Sugar, Cardamom, Saffron', '1. Soak almonds and poppy seeds in water overnight. 2. Blend soaked almonds and poppy seeds with sugar and cardamom. 3. Strain the mixture and serve chilled. 4. Garnish with saffron if desired.'),
('Kachi Haldi ka Halwa', 'Raw turmeric (kachi haldi), Semolina (suji), Ghee, Sugar, Milk, Cardamom, Nuts (cashews, almonds)', '1. Grate raw turmeric and set aside. 2. Heat ghee in a pan and add semolina, roasting until golden. 3. Add grated turmeric, sugar, and milk, and cook until thickened. 4. Stir in cardamom and garnish with nuts. 5. Serve warm.'),
('Choliya', 'Fresh green chickpeas (choliya), Mustard oil, Cumin seeds, Salt, Green chilies, Ginger', '1. Heat mustard oil in a pan and add cumin seeds. 2. Add chopped ginger and green chilies, and sauté. 3. Add fresh green chickpeas and salt, and cook until tender. 4. Serve hot as a side dish.'),
('Paneer Burji', 'Paneer (cottage cheese), Onion, Tomato, Green chilies, Ginger, Spices (cumin, turmeric, garam masala), Salt, Oil', '1. Crumble paneer and set aside. 2. Heat oil in a pan and sauté chopped onions, ginger, and green chilies until golden. 3. Add chopped tomatoes and spices, and cook until soft. 4. Stir in crumbled paneer and cook for a few minutes. 5. Serve hot with roti or bread.'),
('Khatte Chole', 'Chickpeas, Raw mango, Onion, Tomato, Ginger, Spices (cumin, coriander, turmeric), Salt, Oil', '1. Soak and cook chickpeas until soft. 2. Heat oil in a pan and sauté chopped onions, ginger, and tomatoes until golden. 3. Add spices and raw mango, and cook until the mango is soft. 4. Stir in cooked chickpeas and simmer. 5. Serve hot with rice or roti.'),
('Raw Mango Chutney', 'Raw mango, Sugar, Salt, Green chilies, Ginger, Cumin powder', '1. Peel and chop raw mango into small pieces. 2. Blend with sugar, salt, green chilies, ginger, and cumin powder to a smooth paste. 3. Adjust sweetness and spice to taste. 4. Serve as a tangy condiment with meals.'),
('Gulkand', 'Rose petals, Sugar', '1. Wash and dry rose petals. 2. Layer rose petals and sugar in a jar, pressing down to release juices. 3. Seal the jar and let it sit in sunlight for a few days until it forms a thick syrup. 4. Serve as a sweet treat or digestive aid.'),
('Lehsan Gobhi', 'Cauliflower, Garlic, Mustard oil, Cumin seeds, Turmeric powder, Salt, Green chilies', '1. Cut cauliflower into florets and set aside. 2. Heat mustard oil in a pan and add cumin seeds. 3. Add chopped garlic and sauté until golden. 4. Add cauliflower, turmeric powder, salt, and green chilies, and cook until tender. 5. Serve hot with roti or rice.'),
('Achaari Aloo Gobi', 'Potatoes, Cauliflower, Mustard oil, Pickle spices (fenugreek, mustard seeds, kalonji), Turmeric powder, Salt', '1. Heat mustard oil in a pan and add pickle spices. 2. Add chopped potatoes and cauliflower, turmeric powder, and salt. 3. Cook until the vegetables are tender and coated with spices. 4. Serve hot with roti or paratha.'),
('Rajma Masala', 'Kidney beans (rajma), Onion, Tomato, Ginger, Garlic, Spices (cumin, coriander, garam masala), Salt, Oil', '1. Soak and cook kidney beans until soft. 2. Heat oil in a pan and sauté chopped onions, ginger, and garlic until golden. 3. Add chopped tomatoes and spices, and cook until soft. 4. Stir in cooked rajma and simmer. 5. Serve hot with rice or roti.'),
('Chana Madra', 'Chickpeas (chana), Yogurt, Ginger, Garlic, Spices (cumin, coriander, turmeric), Salt, Oil', '1. Soak chickpeas overnight and cook until soft. 2. In a pan, heat oil and sauté ginger and garlic. 3. Add spices and cooked chickpeas, mixing well. 4. Stir in yogurt and simmer for a few minutes. 5. Serve hot with rice or roti.'),
('Siddu', 'Wheat flour, Yeast, Water, Salt, Stuffing (like grated radish, or sweetened coconut)', '1. Prepare a dough with wheat flour, yeast, water, and salt. 2. Let it rise for a few hours. 3. Roll out small discs, fill with stuffing, and seal. 4. Steam the filled dough until cooked through. 5. Serve hot with ghee or chutney.'),
('Dham', 'Rice, Rajma (kidney beans), Yogurt, Spices (cumin, coriander, turmeric), Ginger, Green chilies, Ghee', '1. Cook rice and rajma separately until soft. 2. In a pot, heat ghee and sauté ginger and green chilies. 3. Add cooked rajma, spices, and yogurt, and mix well. 4. Serve the rajma over rice as a complete meal.'),
('Kullu Trout', 'Trout fish, Ginger, Garlic, Lemon juice, Spices (cumin, coriander, turmeric), Salt, Mustard oil', '1. Clean and marinate trout with ginger, garlic, lemon juice, and spices. 2. Heat mustard oil in a pan and fry the marinated trout until cooked through. 3. Serve hot with lemon wedges and salad.'),
('Bhaturu', 'Wheat flour, Water, Salt, Curd, Baking soda', '1. Prepare a dough with wheat flour, water, salt, curd, and baking soda. 2. Let it rest for a few hours. 3. Roll out into flatbreads and deep fry until golden brown. 4. Serve hot with chole or curry.'),
('Aloo Palda', 'Potatoes, Yogurt, Ginger, Green chilies, Spices (cumin, turmeric), Salt, Mustard oil', '1. Boil and mash potatoes. 2. In a pan, heat mustard oil and sauté ginger and green chilies. 3. Add spices and mashed potatoes, mixing well. 4. Stir in yogurt and cook for a few minutes. 5. Serve hot with rice or roti.'),
('Kullu Dham', 'Rice, Rajma (kidney beans), Chana dal, Spices (cumin, coriander, turmeric), Ghee, Yogurt', '1. Cook rice, rajma, and chana dal separately until soft. 2. In a pot, heat ghee and sauté spices. 3. Mix cooked beans and dal with spices and yogurt. 4. Serve over rice as a complete meal.'),
('Sarson ka Saag', 'Mustard greens (sarson), Spinach, Bathua (chenopodium), Ginger, Green chilies, Garlic, Ghee, Salt', '1. Wash and chop the mustard greens, spinach, and bathua. 2. Boil the greens with ginger, green chilies, and garlic until soft. 3. Blend to a coarse paste. 4. Heat ghee in a pan, add the paste, and cook for a few minutes. 5. Serve hot with makki di roti and butter.'),
('Teliya Mah', 'Black gram (urad dal), Mustard oil, Ginger, Green chilies, Spices (cumin, coriander, turmeric), Salt', '1. Soak black gram overnight and cook until soft. 2. Heat mustard oil in a pan, add cumin seeds, ginger, and green chilies. 3. Add cooked dal, spices, and salt, and simmer for a few minutes. 4. Serve hot with rice or roti.'),
('Babru', 'Whole wheat flour, Black gram flour (urad dal), Spices (cumin, coriander), Salt, Oil', '1. Prepare a dough with whole wheat flour and water. 2. Mix black gram flour with spices and water to form a filling. 3. Roll out small discs of dough, fill with the dal mixture, and seal. 4. Deep fry until golden brown. 5. Serve hot with chutney or yogurt.'),
('Pahadi Chicken', 'Chicken, Yogurt, Ginger, Garlic, Green chilies, Spices (cumin, coriander, garam masala), Salt, Mustard oil', '1. Marinate chicken with yogurt, ginger, garlic, green chilies, and spices for a few hours. 2. Heat mustard oil in a pan and add marinated chicken. 3. Cook until the chicken is tender and the oil separates. 4. Serve hot with rice or roti.'),
('Chamba Style Fried Fish', 'Fish (like trout), Ginger, Garlic, Spices (cumin, coriander, turmeric), Salt, Mustard oil', '1. Clean and marinate fish with ginger, garlic, spices, and salt for 30 minutes. 2. Heat mustard oil in a pan and fry the marinated fish until golden brown on both sides. 3. Serve hot with lemon wedges and salad.'),
('Chha Gosht', 'Mutton, Yogurt, Ginger, Garlic, Spices (cumin, coriander, garam masala), Salt, Mustard oil', '1. Marinate mutton with yogurt, ginger, garlic, and spices for a few hours. 2. Heat mustard oil in a pot and add marinated mutton. 3. Cook until the meat is tender and the oil separates. 4. Serve hot with rice or roti.'),
('Gahat ka Shorba', 'Horse gram (gahat), Ginger, Garlic, Spices (cumin, coriander, turmeric), Salt, Water', '1. Soak horse gram overnight and cook until soft. 2. In a pot, heat oil and sauté ginger and garlic. 3. Add cooked horse gram, spices, and water, and simmer for a few minutes. 4. Serve hot as a soup or with rice.'),
('Auriya Kaddu', 'Pumpkin (kaddu), Mustard oil, Cumin seeds, Ginger, Green chilies, Salt', '1. Cut pumpkin into cubes. 2. Heat mustard oil in a pan and add cumin seeds. 3. Add chopped ginger and green chilies, and sauté. 4. Add pumpkin cubes and salt, and cook until tender. 5. Serve hot with roti or rice.'),
('Himachali Tawa Murgh', 'Chicken, Ginger, Garlic, Green chilies, Spices (cumin, coriander, garam masala), Salt, Mustard oil', '1. Marinate chicken with ginger, garlic, green chilies, and spices for a few hours. 2. Heat mustard oil on a tawa (griddle) and add marinated chicken. 3. Cook until the chicken is tender and well-coated with spices. 4. Serve hot with naan or roti.'),
('Shakuli', 'Wheat flour, Water, Salt, Ghee', '1. Prepare a dough with wheat flour, water, and salt. 2. Roll out into flatbreads and cook on a hot griddle. 3. Brush with ghee and serve hot with chutney or yogurt.'),
('Mula Saak Kangshoi', 'Radish greens, Water, Salt, Green chilies', '1. Wash and chop radish greens. 2. Boil water in a pot and add the greens. 3. Add salt and green chilies. 4. Cook until the greens are tender. 5. Serve hot as a stew.'),
('Chak Hao Kheer', 'Black rice, Milk, Sugar, Cardamom', '1. Rinse black rice and soak for a few hours. 2. Cook the rice in milk until soft. 3. Add sugar and cardamom, and simmer for a few minutes. 4. Serve warm or chilled.'),
('Singju', 'Vegetables (cabbage, carrots, etc.), Peanuts, Mustard oil, Salt, Green chilies', '1. Shred the vegetables finely. 2. Mix with crushed peanuts, mustard oil, salt, and green chilies. 3. Toss well and serve as a salad.'),
('Chana Kanghou', 'Chickpeas, Onion, Green chilies, Salt, Mustard oil', '1. Boil chickpeas until soft. 2. In a pan, heat mustard oil and sauté chopped onions and green chilies. 3. Add boiled chickpeas and salt, and cook for a few minutes. 4. Serve hot.'),
('Yen Thongba', 'Chicken, Potatoes, Spring onions, Ginger, Garlic, Spices', '1. Marinate chicken with ginger, garlic, and spices. 2. In a pot, heat oil and sauté marinated chicken. 3. Add potatoes and water, and cook until tender. 4. Garnish with spring onions and serve hot.'),
('Manipuri Mangal Ooti', 'Yellow or green peas, Cooking soda, Salt, Mustard oil, Cumin seeds, Red chillies, Bay leaf, Grated ginger, Chopped onions', '1. Soak the peas overnight or for a minimum of 4 hours. 2. Pressure cook the peas with salt and cooking soda until soft. 3. Heat mustard oil in a pan, add cumin seeds, bay leaf, and red chillies. 4. Add grated ginger and chopped onions, sauté until onions are translucent. 5. Pour the tempering over the cooked peas and mix well. Serve with rice, Poori, or chapathi.'),
('Eromba', 'Boiled vegetables (like potatoes, brinjal), Fermented fish (or any fish), Chilli powder, Salt', '1. Boil the vegetables until soft. 2. Mash the boiled vegetables in a bowl. 3. Add fermented fish, chilli powder, and salt. 4. Mix well and serve as a side dish with rice.'),
('Kangshoi', 'Seasonal vegetables (like spinach, mustard greens), Water, Salt, Green chillies', '1. Wash and chop the vegetables. 2. Boil water in a pot and add the vegetables. 3. Add salt and green chillies. 4. Cook until the vegetables are tender. 5. Serve hot as a light stew.'),
('Ngari Khongba', 'Fermented fish (ngari), Vegetables (like potatoes, brinjal), Mustard oil, Salt', '1. Heat mustard oil in a pan. 2. Add chopped vegetables and sauté until slightly soft. 3. Add fermented fish and salt, and cook for a few minutes. 4. Serve hot with rice.'),
('Kangshoi with Fish', 'Seasonal vegetables, Fish (like tilapia), Water, Salt, Green chilies', '1. Wash and chop the vegetables. 2. Boil water in a pot and add the vegetables and fish. 3. Add salt and green chilies. 4. Cook until the fish is done and vegetables are tender. 5. Serve hot with rice.'),
('Paan Naga', 'Betel leaves, Spices, Nuts, Dried fish', '1. Wash betel leaves and lay them flat. 2. Mix spices, nuts, and dried fish in a bowl. 3. Place the mixture on the leaves and fold them. 4. Serve as a snack or appetizer.'),
('Koat Pitha', 'Rice flour, Banana, Jaggery, Water', '1. Mix rice flour with water to form a dough. 2. Mash bananas and mix with jaggery. 3. Shape the dough into small cups, fill with the banana mixture, and steam until cooked.'),
('Bai', 'Vegetables (like spinach, mustard greens), Salt, Fermented soybeans', '1. Boil the vegetables until tender. 2. Add salt and fermented soybeans, and cook for a few more minutes. 3. Serve hot with rice.'),
('Sanpiau', 'Sticky rice, Water, Salt, Garlic', '1. Cook sticky rice with water and salt until soft. 2. Serve hot, often enjoyed as a snack or light meal.'),
('Misa Mach Poora', 'Prawns, Mustard oil, Lemon juice, Spices', '1. Marinate prawns with mustard oil, lemon juice, and spices. 2. Grill until cooked through and serve hot.'),
('Dawrpui', 'Pork, Bamboo shoots, Ginger, Garlic, Spices', '1. Cut pork into pieces and marinate with ginger, garlic, and spices. 2. Cook in a pot with bamboo shoots until tender. 3. Serve hot with rice.'),
('Vawksa Rep', 'Smoked pork, Mustard leaves, Ginger, Garlic, Salt', '1. Cook smoked pork with chopped mustard leaves, ginger, garlic, and salt until everything is well combined. 2. Serve hot with rice.'),
('Khawthluk', 'Sticky rice, Coconut milk, Jaggery', '1. Cook sticky rice and mix with coconut milk and jaggery. 2. Serve warm as a dessert or snack.'),
('Bai Kham', 'Vegetables (like pumpkin, beans), Fermented soybeans, Salt', '1. Boil the vegetables until soft. 2. Add fermented soybeans and salt, and cook for a few more minutes. 3. Serve hot with rice.'),
('Mizo Thukpa', 'Noodles, Vegetables, Chicken or pork, Spices, Broth', '1. Cook noodles in broth with vegetables and meat. 2. Season with spices and serve hot.'),
('Pawndum', 'Rice, Fish, Mustard oil, Spices', '1. Cook rice and set aside. 2. Fry fish in mustard oil with spices. 3. Serve fish over rice.'),
('Pakhala Bhata', 'Cooked rice, Water, Curd, Salt', '1. Soak cooked rice in water overnight. 2. Serve with curd, fried fish, and side dishes like papad and Badi Choora.'),
('Dalma', 'Moong dal, Mixed vegetables (pumpkin, plantain, papaya), Spices', '1. Cook moong dal with chopped vegetables and spices. 2. Serve hot with rice.'),
('Chingudi Jhola', 'Prawns, Onion, Tomato, Ginger-garlic paste, Spices', '1. Sauté onions and tomatoes, add ginger-garlic paste, and cook prawns with spices. 2. Serve with rice.'),
('Kanika', 'Basmati rice, Ghee, Sugar, Dry fruits, Spices', '1. Cook basmati rice with ghee, sugar, and spices. 2. Garnish with dry fruits and serve.'),
('Chhena Poda', 'Chhena (cottage cheese), Sugar, Semolina, Cardamom', '1. Mix chhena with sugar and semolina, bake until golden brown. 2. Serve as a dessert.'),
('Macha Ghanta', 'Fish, Onion, Tomato, Spices', '1. Fry fish and prepare a gravy with onions and tomatoes. 2. Serve with steamed rice.'),
('Gupchup', 'Hollow puris, Chana masala, Jal jeera', '1. Fill hollow puris with chana masala and dip in jal jeera. 2. Serve as a snack.'),
('Aloo Potala Rasa', 'Potatoes, Pointed gourds, Onion, Spices', '1. Cook potatoes and pointed gourds in a spiced gravy. 2. Serve with rice.'),
('Rasabali', 'Chhena, Milk, Cardamom, Sugar', '1. Fry chhena fritters and soak in sweetened milk flavoured with cardamom. 2. Serve chilled.'),
('Chakuli Pitha', 'Rice, Urad dal, Mustard oil', '1. Prepare a batter of soaked rice and urad dal, cook on a griddle. 2. Serve with chutney or curry.'),
('Dahi Pakhala', 'Cooked rice, Curd, Water, Salt, Green chilies', '1. Mix cooked rice with water and curd. 2. Add salt and chopped green chilies. 3. Serve chilled, often enjoyed in summer.'),
('Badi Chura', 'Badi (dried lentil cakes), Onion, Green chilies, Mustard oil', '1. Crush fried badi and mix with chopped onions and green chilies. 2. Drizzle with mustard oil and serve as a side dish.'),
('Prawn Malai Curry', 'Prawns, Coconut milk, Onion, Ginger-garlic paste, Spices', '1. Sauté onions and ginger-garlic paste, add prawns and coconut milk. 2. Cook until prawns are tender and serve with rice.'),
('Bitter Gourd Stir Fry', 'Bitter gourd, Onion, Mustard seeds, Turmeric, Salt', '1. Slice bitter gourd and sauté with onions and mustard seeds. 2. Add turmeric and salt, cook until tender.');